import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './EventsTable.css';

const EventsTable = () => {
    const [events, setEvents] = useState([]);
    const [loading, setLoading] = useState(true);
    const [userRole, setUserRole] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        const token = sessionStorage.getItem('token');

        if (!token) {
            navigate('/login');
            return;
        }

        const fetchData = async () => {
            try {
                const response = await fetch('/api/events', {
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (!response.ok) {
                    throw new Error('Ошибка при загрузке событий.');
                }

                const data = await response.json();
                setEvents(data);

                // Получаем роль пользователя из токена
                const { role } = getUserInfoFromToken(token);
                setUserRole(role);

                setLoading(false);
            } catch (error) {
                console.error('Ошибка:', error);
                setLoading(false);
            }
        };

        fetchData();
    }, [navigate]);

    // Функция для получения информации пользователя из JWT токена
    const getUserInfoFromToken = (token) => {
        try {
            const base64Url = token.split('.')[1];
            const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
            const jsonPayload = decodeURIComponent(atob(base64).split('').map(c => {
                return '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2);
            }).join(''));

            const payload = JSON.parse(jsonPayload);
            return {
                role: payload.role || payload['http://schemas.microsoft.com/ws/2008/06/identity/claims/role']
            };
        } catch (error) {
            console.error('Ошибка при получении информации из токена:', error);
            return { role: null };
        }
    };

    if (loading) {
        return <p>Загрузка...</p>;
    }

    return (
        <div className="events-table-container">
            <h2>События</h2>
            {userRole === 'Admin' && (
                <Link to="/create-event" className="create-event-button">Создать событие</Link>
            )}
            <table className="events-table">
                <thead>
                    <tr>
                        <th>Название</th>
                        <th>Дата начала</th>
                        <th>Категория</th>
                        <th>Действия</th>
                    </tr>
                </thead>
                <tbody>
                    {events.map(event => (
                        <tr key={event.id}>
                            <td>{event.title}</td>
                            <td>{event.startDate}</td>
                            <td>{event.category}</td>
                            <td>
                                <Link to={`/events/${event.id}`}>Подробнее</Link>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default EventsTable;
