// src/Login.js
import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import './Login.css'; // Подключаем файл стилей

const Login = () => {
    const [name, setName] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (event) => {
        event.preventDefault();
        
        const credentials = {
            name: name,
            password: password
        };

        try {
            const response = await fetch('/api/auth/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(credentials) // Преобразование объекта в JSON строку
            });

            if (!response.ok) {
                throw new Error('Ошибка входа');
            }

            const result = await response.json();
            console.log('Успешный вход:', result);

            // Сохранение токена в localStorage
            localStorage.setItem('token', result.token);

            // Перенаправление пользователя на страницу Dashboard
            navigate('/dashboard');
        } catch (error) {
            console.error('Ошибка:', error);
        }
    };

    return (
        <div className="login-container">
            <h2>Вход в аккаунт</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label htmlFor="name">Имя пользователя:</label>
                    <input
                        type="text"
                        id="name"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="password">Пароль:</label>
                    <input
                        type="password"
                        id="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </div>
                <button type="submit">Войти</button>
            </form>
            <p>Нет аккаунта? <Link to="/register">Зарегистрируйтесь</Link></p> {/* Ссылка на страницу регистрации */}
            <button>
                <Link to="/register">Регистрация</Link> {/* Кнопка для перехода на страницу регистрации */}
            </button>
        </div>
    );
};

export default Login;
