import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './EventDetails.css';

const EventDetails = () => {
    const { id } = useParams();
    const [event, setEvent] = useState(null);
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const token = sessionStorage.getItem('token');

        const fetchEventDetails = fetch(`/api/events/${id}`, {
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${token}`
            }
        });

        const fetchEventUsers = fetch(`/api/events/${id}/users`, {
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${token}`
            }
        });

        Promise.all([fetchEventDetails, fetchEventUsers])
            .then(async ([eventRes, usersRes]) => {
                if (!eventRes.ok) {
                    throw new Error('Ошибка при загрузке деталей события');
                }
                if (!usersRes.ok) {
                    throw new Error('Ошибка при загрузке пользователей');
                }

                const eventData = await eventRes.json();
                const usersData = await usersRes.json();

                setEvent(eventData);
                setUsers(usersData);
                setLoading(false);
            })
            .catch(error => {
                console.error('Ошибка:', error);
                setLoading(false);
            });
    }, [id]);

    if (loading) {
        return <p>Загрузка...</p>;
    }

    

if (!event) {
    return <p>Событие не найдено.</p>;
}

return (
    <div className="event-details-container">
        <h2>{event.title}</h2>
        {event.imageUrl && <img src={event.imageUrl} alt={event.title} />}
        <p><strong>Описание:</strong> {event.description}</p>
        <p><strong>Дата начала:</strong> {event.startDate}</p>
        <p><strong>Категория:</strong> {event.category}</p>
        <p><strong>Максимальное количество участников:</strong> {event.maxNumberOfUsers}</p>

        <h3>Записавшиеся пользователи:</h3>
        {users.length > 0 ? (
            <ul>
                {users.map(user => (
                    <li key={user.id}>
                        <div className="user-info">
                            {user.name} {user.surename} ({user.email})
                        </div>
                    </li>
                ))}
            </ul>
        ) : (
            <p className="no-users">Никто не записался на это событие.</p>
        )}
    </div>
    );
};

export default EventDetails;
