// src/EventDetails.js
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './EventDetails.css';

const EventDetails = () => {
  const { eventId } = useParams(); // Получаем идентификатор события из URL
  const navigate = useNavigate();
  const [event, setEvent] = useState(null);
  const [isRegistered, setIsRegistered] = useState(false);
  const [loading, setLoading] = useState(true);
  const [users, setUsers] = useState([]);

  // Получаем userId из sessionStorage
  const userId = sessionStorage.getItem('userId');

  // Перенаправляем на страницу входа, если пользователь не аутентифицирован
  useEffect(() => {
    if (!userId) {
      navigate('/login');
    }
  }, [navigate, userId]);

  useEffect(() => {
    const fetchEventData = async () => {
      try {
        // Получаем детали события
        const eventResponse = await fetch(`/api/events/${eventId}`);
        if (!eventResponse.ok) {
          throw new Error('Ошибка при загрузке деталей события.');
        }
        const eventData = await eventResponse.json();
        setEvent(eventData);

        // Получаем список пользователей, записавшихся на событие
        const usersResponse = await fetch(`/api/events/${eventId}/users`);
        if (!usersResponse.ok) {
          throw new Error('Ошибка при загрузке списка пользователей.');
        }
        const usersData = await usersResponse.json();
        setUsers(usersData);

        // Проверяем, записан ли текущий пользователь на событие
        const registered = usersData.some(user => user.id === parseInt(userId));
        setIsRegistered(registered);

        setLoading(false);
      } catch (error) {
        console.error('Ошибка:', error);
        setLoading(false);
      }
    };

    fetchEventData();
  }, [eventId, userId]);

  const handleRegister = async () => {
    try {
      const response = await fetch(`/api/events/${eventId}/register/${userId}`, {
        method: 'PUT',
      });

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(errorText);
      }

      setIsRegistered(true);
      alert('Вы успешно зарегистрировались на событие!');

      // Обновляем список пользователей
      const usersResponse = await fetch(`/api/events/${eventId}/users`);
      if (usersResponse.ok) {
        const usersData = await usersResponse.json();
        setUsers(usersData);
      }
    } catch (error) {
      console.error('Ошибка:', error);
      alert(`Ошибка: ${error.message}`);
    }
  };

  const handleUnregister = async () => {
    try {
      const response = await fetch(`/api/events/${eventId}/unregister/${userId}`, {
        method: 'PUT',
      });

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(errorText);
      }

      setIsRegistered(false);
      alert('Вы успешно отменили регистрацию на событие!');

      // Обновляем список пользователей
      const usersResponse = await fetch(`/api/events/${eventId}/users`);
      if (usersResponse.ok) {
        const usersData = await usersResponse.json();
        setUsers(usersData);
      }
    } catch (error) {
      console.error('Ошибка:', error);
      alert(`Ошибка: ${error.message}`);
    }
  };

  if (loading) {
    return <p>Загрузка...</p>;
  }

  if (!event) {
    return <p>Событие не найдено.</p>;
  }

  const canRegister = users.length < event.maxNumberOfUsers && !isRegistered;

  return (
    <div className="event-details-container">
      <h2>{event.title}</h2>
      {event.imageUrl && <img src={event.imageUrl} alt={event.title} />}
      <p><strong>Описание:</strong> {event.description}</p>
      <p><strong>Дата начала:</strong> {event.startDate}</p>
      <p><strong>Категория:</strong> {event.category}</p>
      <p><strong>Максимальное количество участников:</strong> {event.maxNumberOfUsers}</p>

      {canRegister && (
        <button onClick={handleRegister}>Записаться на событие</button>
      )}

      {isRegistered && (
        <button onClick={handleUnregister}>Отменить регистрацию</button>
      )}

      {(!canRegister && !isRegistered && users.length >= event.maxNumberOfUsers) && (
        <p>Достигнуто максимальное количество участников.</p>
      )}

      <h3>Записавшиеся пользователи:</h3>
      {users.length > 0 ? (
        <ul>
          {users.map(user => (
            <li key={user.id}>
              {user.name} {user.surename} ({user.email})
            </li>
          ))}
        </ul>
      ) : (
        <p>Никто не записался на это событие.</p>
      )}
    </div>
  );
};

export default EventDetails;
