// src/CreateEvent.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './CreateEvent.css';

const CreateEvent = () => {
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [startDate, setStartDate] = useState('');
    const [category, setCategory] = useState('');
    const [maxNumberOfUsers, setMaxNumberOfUsers] = useState('');
    const [image, setImage] = useState(null);
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        const token = sessionStorage.getItem('token');

        const formData = new FormData();
        formData.append('Title', title);
        formData.append('Description', description);
        formData.append('StartDate', startDate);
        formData.append('Category', category);
        formData.append('MaxNumberOfUsers', maxNumberOfUsers);

        try {
            const response = await fetch('/api/events', {
                method: 'POST',
                headers: {
                    'Authorization': `Bearer ${token}`
                },
                body: formData
            });

            if (!response.ok) {
                const errorText = await response.text();
                throw new Error(errorText);
            }

            alert('Событие успешно создано!');
            navigate('/events');
        } catch (error) {
            console.error('Ошибка при создании события:', error);
            alert(`Ошибка: ${error.message}`);
        }
    };

    const handleImageChange = (e) => {
        setImage(e.target.files[0]);
    };

    return (
        <div className="create-event-container">
            <h2>Создать новое событие</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label htmlFor="title">Название:</label>
                    <input
                        type="text"
                        id="title"
                        value={title}
                        onChange={(e) => setTitle(e.target.value)}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="description">Описание:</label>
                    <textarea
                        id="description"
                        value={description}
                        onChange={(e) => setDescription(e.target.value)}
                        required
                    ></textarea>
                </div>
                <div className="form-group">
                    <label htmlFor="startDate">Дата начала:</label>
                    <input
                        type="date"
                        id="startDate"
                        value={startDate}
                        onChange={(e) => setStartDate(e.target.value)}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="category">Категория:</label>
                    <input
                        type="text"
                        id="category"
                        value={category}
                        onChange={(e) => setCategory(e.target.value)}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="maxNumberOfUsers">Максимальное количество участников:</label>
                    <input
                        type="number"
                        id="maxNumberOfUsers"
                        value={maxNumberOfUsers}
                        onChange={(e) => setMaxNumberOfUsers(e.target.value)}
                        required
                    />
                </div>
                <button type="submit">Создать событие</button>
            </form>
        </div>
    );
};

export default CreateEvent;
