// src/EventsTable.js
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './EventsTable.css';

const EventsTable = () => {
    const [events, setEvents] = useState([]);

    useEffect(() => {
        const fetchEvents = async () => {
            try {
                const token = sessionStorage.getItem('token');
                const response = await fetch('/api/events', {
                    method: 'GET',
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${token}` // Добавляем токен в заголовок
                    }
                });

                if (!response.ok) {
                    throw new Error('Ошибка загрузки событий');
                }

                const data = await response.json();
                setEvents(data);
            } catch (error) {
                console.error('Ошибка при загрузке событий:', error);
            }
        };

        fetchEvents();
    }, []);

    return (
        <div className="events-table-container">
            <h2>Список событий</h2>
            {userRole === 'Admin' && (
                <Link to="/create-event" className="create-event-button">Создать событие</Link>
            )}
            <table className="events-table">
                <thead>
                    <tr>
                        <th>Изображение</th>
                        <th>Название</th>
                        <th>Описание</th>
                        <th>Дата начала</th>
                        <th>Категория</th>
                        <th>Макс. кол-во пользователей</th>
                        <th>Действия</th>
                    </tr>
                </thead>
                <tbody>
                    {events.map(event => (
                        <tr key={event.id}>
                            <td>
                                {event.imageUrl ? (
                                    <img src={event.imageUrl} alt={event.title} className="event-image" />
                                ) : (
                                    'Нет изображения'
                                )}
                            </td>
                            <td>{event.title}</td>
                            <td>{event.description}</td>
                            <td>{event.startDate}</td>
                            <td>{event.category}</td>
                            <td>{event.maxNumberOfUsers}</td>
                            <td>
                                <Link to={`/events/${event.id}`}>Перейти</Link>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default EventsTable;
